xt = 0
yt = 20
zt = 10

GRID_RADIUS = 500

Xu = [-104.97194528, 92.56543584, -72.01715391, -33.41203811, 57.67787989, 11.61554018, -201.74854545, 32.21894151, 11.21529191, 276.29252881, -195.337314, 56.32218626]

